import axios from "axios";

var BASE_URL = process.env.VUE_APP_API_KBS_ENDPOINT;

export default {
  getDeclarationDetail(params) {
    // axios.defaults.headers.common["Authorization"] = "XXXXXX";
    return axios.get(BASE_URL + "/api/v1/article", {
      params: params,
      headers: { Authorization: "Bearer XXXXX" }
    });
  },
  saveArticle(article) {
    var url = process.env.VUE_APP_API_LEP_ENDPOINT + "/api/v1/article";
    var requestBody = {
      article_plain: JSON.stringify(article.plain),
      article_quill: JSON.stringify(article.quill)
    };
    var config = {
      headers: {
        "Content-Type": "application/x-www-form-urlencoded"
      }
    };

    var querystring = require("querystring");

    return axios.post(url, querystring.stringify(requestBody), config);
  }
};
